import { useState } from "react";
import BoardPage from "../components/pages/board";
import data from "../services/data.json";
import { getAccordionOptions, getSearchOptions } from "../utilities/utilities";
import "normalize.css";
import "./board.css";

const BoardView = () => {
    const [input, setInput] = useState("");
    const [jobs, setJobs] = useState(data);

    const onSelectOption = () => {
        console.log("heelo");
    }

    const handleSearch = (e) => {
        const userInput = e.target.value;
        setInput(userInput);
        setJobs(getSearchOptions(userInput))
        return "search";
    }

    return <BoardPage jobs={jobs} accordionOptions={getAccordionOptions(onSelectOption)} handleSearch={handleSearch} searchInput={input}/>
}

export default BoardView;
