import { useState } from "react";
import JobInput from "../../atoms/job-input/job-input";
import AccordionCheckbox from "../../molecules/accordion-checkbox/accordion-checkbox";
import { AccordionCheckboxType } from "../../molecules/accordion-checkbox/accordion-checkbox-type";
import "./side-nav.css";

interface Props {
    clearFilters: () => void,
    accordionOptions: AccordionCheckboxType[]
}

const SideNav = ({clearFilters, accordionOptions}: Props) => {

    const [showNav, setShowNav] = useState(false);
    const [active, setActiveNav] = useState(false);

    const handleEditFilter = () => {
        setShowNav(!showNav);
        setActiveNav(true);
    }

    return (
        <>
            <div className={`side-nav ${active ? 'active' : ''}`}>
                <div className="side-nav-content">
                    <div className="filter-section">
                        <h3>What do you want to do?</h3>
                        <a href="" onClick={clearFilters}>Clear Filters</a>
                    <JobInput/>
                    </div>
                    {
                        accordionOptions.map((option) => {
                            return <AccordionCheckbox key={option.id} id={option.id} label={option.label} options={option.options} />
                        }) 
                    }
                </div>
            </div>
            <div className="side-nav-overlay"></div>
            <a className="edit-filters"  href="" onClick={handleEditFilter}>Edit Filters</a>
        </>
     );
};

export default SideNav;
