import data from "../services/data.json";

const getLabelOptions = (label) => {
    return data.reduce((option) => option[label], []);
}

export const getAccordionOptions = () => {
    const accordionOptions = ["location", "contract"];

    accordionOptions.map((option, index) => {
        return {id: index, label: option, options: getLabelOptions(option)}
    });

    return [];
}
