import { useState, useEffect } from "react";
import BoardPage from "../components/pages/board";
import data from "../services/data.json";
import { getAccordionOptions, getSearchOptions, getFilteredOptions, getAccordionFilters } from "../utilities/utilities";
import "normalize.css";
import "./board.css";

const BoardView = () => {
    const [input, setInput] = useState("");
    const [jobs, setJobs] = useState(data);
    const [filterKeywords, setfilterKeywords] = useState(getAccordionFilters);

    const onSelectFilterOption = (e) => {
       const optionSelected = e.target.getAttribute("data-parent");
       const keyword = e.target.name;

       if (!filterKeywords.get(optionSelected).some(key => key === keyword)) {
            const prevKeys = filterKeywords.get(optionSelected);
            setfilterKeywords(filterKeywords.set(optionSelected, [...prevKeys, keyword]));
       }
       else {
           const newKeys = filterKeywords.get(optionSelected).filter((key) => key.keyword === keyword)
        setfilterKeywords(filterKeywords.set(optionSelected, [...newKeys]));
       }
    }

    const handleSearch = (e) => {
        const userInput = e.target.value;
        setInput(userInput);
        setJobs(getSearchOptions(userInput));
    }

    const clearAll = () => {
        setfilterKeywords([]);
    };


    useEffect(() => {
        const filteredOptions = getFilteredOptions(filterKeywords).flat();

        if(filteredOptions.length) 
            setJobs(filteredOptions);
        else // error case
            setJobs(data);
    }, [filterKeywords]);

    return <BoardPage jobs={jobs} accordionOptions={getAccordionOptions(onSelectFilterOption)} handleSearch={handleSearch} searchInput={input}/>
}

export default BoardView;
