import { convertCompilerOptionsFromJson } from "typescript";
import data from "../services/data.json";

const getLabelOptions = (label) => {
    return data.reduce((option) => option[label], []);
}

export const getAccordionOptions = (onChange) => {
    const accordionOptions = ["location", "contract"];

    const res = accordionOptions.map((option, index) => {
        console.log({id: index, label: option, options: [{label: "USA Only", onChange}]});
        return {id: index, label: option, options: [{label: "USA Only", onChange}]}
    });

    console.log(res);

    return [];
}
