import { useState, useEffect } from "react";
import BoardPage from "../components/pages/board";
import data from "../services/data.json";
import { getAccordionOptions, getSearchOptions, getFilteredOptions } from "../utilities/utilities";
import "normalize.css";
import "./board.css";

const BoardView = () => {
    const [input, setInput] = useState("");
    const [jobs, setJobs] = useState(data);
    const [filterKeywords, setfilterKeywords] = useState([]);

    const onSelectFilterOption = (e) => {
       const optionSelected = e.target.getAttribute("data-parent");
       const keyword = e.target.name;
       if (!filterKeywords.includes({keyword: keyword, optionSelected: optionSelected})) {
        setfilterKeywords([...filterKeywords, {keyword, optionSelected}]);
       }
       else {
        setfilterKeywords(filterKeywords.filter((key) => key.keyword !== keyword));
       }
    }

    const handleSearch = (e) => {
        const userInput = e.target.value;
        setInput(userInput);
        setJobs(getSearchOptions(userInput));
    }

    const clearAll = () => {
        setfilterKeywords([]);
    };


    useEffect(() => {
        console.log("useEffect", filterKeywords);
        //console.log(getFilteredOptions(optionSelected, keyword));

    }, [filterKeywords]);

    return <BoardPage jobs={jobs} accordionOptions={getAccordionOptions(onSelectFilterOption)} handleSearch={handleSearch} searchInput={input}/>
}

export default BoardView;
