import data from "../services/data.json";

const getLabelOptions = (label, onSelectOption) => {
    return data.reduce((acc, option) => {
        if (!acc.some(e => e.label === option[label]))
            acc.push({label: option[label], onSelectOption});

        return acc;
    }, []);
}

export const getAccordionOptions = (onSelectOption) => {
    const accordionOptions = ["location", "contract", "level"];

    return accordionOptions.map((option, index) => {
        return {id: `${index}`, label: option, options: getLabelOptions(option, onSelectOption)};
    });
}

export const getSearchOptions = (keyword, onSelectOption) => {
