import { useState } from "react";
import BoardPage from "../components/pages/board";
import data from "../services/data.json";
import { getAccordionOptions, getSearchOptions } from "../utilities/utilities";
import "normalize.css";
import "./board.css";

const BoardView = () => {
    const [input, setInput] = useState("");
    const [jobs, setJobs] = useState(data);
    const [filterKeywords, setfilterKeywords] = useState([]);

    const onSelectFilterOption = (filter) => {
        setfilterKeywords(filter);
    }

    const handleSearch = (e) => {
        const userInput = e.target.value;
        setInput(userInput);
        setJobs(getSearchOptions(userInput));
    }

    return <BoardPage jobs={jobs} accordionOptions={getAccordionOptions(onSelectFilterOption)} handleSearch={handleSearch} searchInput={input}/>
}

export default BoardView;
