import data from "../services/data.json";

const getOptionsbyKey = (key, onSelectOption) => {
    return data.reduce((acc, option) => {
        if (!acc.some(e => e.label === option[key]))
            acc.push({label: option[key], onSelectOption});

        return acc;
    }, []);
}

export const getAccordionOptions = (onSelectOption) => {
    const accordionOptions = ["location", "contract", "level"];
    return accordionOptions.map((option, index) => {
        return {id: `${index}`, label: option, options: getOptionsbyKey(option, onSelectOption)};
    });
}
