import "normalize.css";
import "./board.css";

const BoardView = () => {
    const [input, setInput] = useState("");
    const [jobs, setJobs] = useState(data);
    const [filterKeywords, setfilterKeywords] = useState(getAccordionFilters);
    const [filterKeywordsObj, setfilterKeywordsObj] = useState([]);
    const [keyword, seteyword] = useState("");
    const [optionSelected, setOptionSelected] = useState("");

    const onSelectFilterOption = (e) => {
       const optionSelected = e.target.getAttribute("data-parent");
       const keyword = e.target.name;

    //    const test = newIdea2(keyword, optionSelected, jobs);
    //    console.log("newIdea", newIdea2(keyword, optionSelected, jobs));
    //    setJobs(test);

        if(!filterKeywordsObj.includes(keyword)) {
            setfilterKeywordsObj([...filterKeywordsObj, keyword]);
            seteyword(keyword);
            setOptionSelected(optionSelected);
        }
        else {
            const hello = filterKeywordsObj.filter((e) => e !== keyword);
            setfilterKeywordsObj(hello);
        }

       if (!filterKeywords.get(optionSelected).some(key => key === keyword)) { 
            const prevKeys = filterKeywords.get(optionSelected);
            setfilterKeywords(new Map(filterKeywords.set(optionSelected, [...prevKeys, keyword])));
       }
       else {
            const newKeys = filterKeywords.get(optionSelected).filter((key) => key !== keyword);
            setfilterKeywords(new Map(filterKeywords.set(optionSelected, [...newKeys])));
       }
    }

    const handleSearch = (e) => {
        const userInput = e.target.value;
        setInput(userInput);
        setJobs(getSearchOptions(userInput));
    }

    const clearAll = () => {
        setfilterKeywords(getAccordionFilters);
    };


    useEffect(() => {
        // const filteredOptions = getFilteredJobsByOption(filterKeywords);
        console.log(filterKeywordsObj);
        // if(filteredOptions.length) 
        //     setJobs(filteredOptions);
        // else // error case
        //     setJobs(data);

        const hel = newIdea2(keyword, optionSelected, jobs);
        console.log(hel);
        if(hel.length) setJobs(hel);
        else setJobs(data);

    }, [filterKeywordsObj, keyword, optionSelected]);

    return <BoardPage jobs={jobs} accordionOptions={getAccordionOptions(onSelectFilterOption)} handleSearch={handleSearch} searchInput={input}/>
}

export default BoardView;
